mapper.select:
select
    rsab.id,
    rsab.module_id ,
    rsab.bill_no ,
    rsab.bill_date ,
    rbt.name businessType,
    rsab.manual_id ,
    rc.code channelCode,
    rsab.notes ,
    rsab.status ,
    rsab.created_time ,
    rsab.updated_time ,
    rsab.check_time
from
    rbp_stock_adjust_bill rsab
    left join rbp_business_type rbt on
    rbt.id = rsab.business_type_id
    left join rbp_channel rc on
    rc.id = rsab.channel_id


        and rsab.module_id = #{query.moduleId}


        and rsab.bill_no = #{query.billNo}


        and rsab.bill_date = #{query.billDate}


        and rsab.manual_id = #{query.manualId}


        and rsab.notes like  concat('%', #{query.notes}, '%')


        and rsab.status in

            #{sta}



        and rbt.name in

            #{bus}



        and rc.code in

            #{cc}



        and rsab.created_time >= #{query.createdDateStart}


        and rsab.created_time <= #{query.createdDateEnd}


        and rsab.check_time >= #{query.checkDateStart}


        and rsab.check_time <= #{query.checkDateEnd}


        and rsab.updated_time >= #{query.updatedDateStart}


        and rsab.updated_time <= #{query.updatedDateEnd}
